// profile.jsx

import  { useState } from 'react';
import axios from 'axios';
import './Profile.css'; // Import CSS file for styling
import { Link } from "react-router-dom";
const Profile = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    degree: '',
    educationStatus: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/profile', formData);
      console.log(response.data);
      // const { firstName, lastName } = formData;
      // updateFullName(`${firstName} ${lastName}`); 
      // Optionally, you can redirect the user to another page after successful submission
    } catch (error) {
      console.error('Error submitting profile:', error);
    }
  };

  return (
    <div>

<div className="d-flex justify-content-center align-items-center text-center vh-100" style= {{backgroundImage : "linear-gradient(#00d5ff,#0095ff,rgba(93,0,255,.555))"}}>

    <div className="bg-white p-3 rounded" style ={{width:'40%'}}> {/* Apply a class for overall container styling */}
      <h3 className="profile-heading">Built Profile</h3>
      <form onSubmit={handleSubmit} className="profile-form"> {/* Apply a class for form styling */}
        <div className="mb-2 text-start">
          <label className="form-label">First Name:</label>
          <input
            type="text"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            className="form-input"
          />
        </div>
        <div className="mb-2 text-start">
          <label className="form-label">Middle Name:</label>
          <input
            type="text"
            name="middleName"
            value={formData.middleName}
            onChange={handleChange}
            className="form-input"
          />
        </div>
        <div className="mb-2 text-start">
          <label className="form-label">Last Name:</label>
          <input
            type="text"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            className="form-input"
          />
        </div>
        <div className="mb-2 text-start">
          <label className="form-label">Highest Degree:</label>
          <select
            type="text"
            name="degree"
            value={formData.degree}
            onChange={handleChange}
            className="form-input"
          >
            <option value="">Select</option>
            <option value="Higher Secondary Education">Higher Secondary Education</option>
            <option value="B.Ed">B.Ed</option>
            <option value="B.tech">B.tech</option>
            <option value="Bsc">Bsc</option>
            <option value="Msc">Msc</option>
            <option value="MCA">MCA</option>
            <option value="Other">Other</option>
          </select>
        </div>
        <div className="mb-3 text-start">
          <label className="form-label">Education Status:</label>
          <select
            name="educationStatus"
            value={formData.educationStatus}
            onChange={handleChange}
            className="form-input"
          >
            <option value="">Select</option>
            <option value="Student">Student</option>
            <option value="Teacher">Teacher</option>
            
          </select>
        </div>
        
        <Link to='/' className="submit-button">Submit</Link>
      </form>
    </div>
    </div>
    </div>
  );
};

export default Profile;
