// profile.jsx

import React, { useState } from 'react';
import axios from 'axios';
import './Profile.css'; // Import CSS file for styling
import { Link } from "react-router-dom";
const Profile = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    degree: '',
    educationStatus: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/profile', formData);
      console.log(response.data);
      // Optionally, you can redirect the user to another page after successful submission
    } catch (error) {
      console.error('Error submitting profile:', error);
    }
  };

  return (
    <div className="profile-container"> {/* Apply a class for overall container styling */}
      <h2 className="profile-heading">Built Profile</h2>
      <form onSubmit={handleSubmit} className="profile-form"> {/* Apply a class for form styling */}
        <div className="form-group">
          <label className="form-label">First Name:</label>
          <input
            type="text"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Middle Name:</label>
          <input
            type="text"
            name="middleName"
            value={formData.middleName}
            onChange={handleChange}
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Last Name:</label>
          <input
            type="text"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Highest Degree:</label>
          <select
            type="text"
            name="degree"
            value={formData.degree}
            onChange={handleChange}
            className="form-input"
          >
            <option value="">Select</option>
            <option value="Higher Secondary Education">Higher Secondary Education</option>
            <option value="B.Ed">B.Ed</option>
            <option value="B.tech">B.tech</option>
            <option value="Teacher">Bsc</option>
            <option value="Teacher">Msc</option>
            <option value="Teacher">MCA</option>
            <option value="Teacher">Other</option>
          </select>
        </div>
        <div className="form-group">
          <label className="form-label">Education Status:</label>
          <select
            name="educationStatus"
            value={formData.educationStatus}
            onChange={handleChange}
            className="form-input"
          >
            <option value="">Select</option>
            <option value="Student">Student</option>
            <option value="Teacher">Teacher</option>
            
          </select>
        </div>
        
        <Link to='/' className="submit-button">Submit</Link>
      </form>
    </div>
  );
};

export default Profile;
